mapper.select:
SELECT r.id, r.parent_id, r.name, r.resource_identifying, r.type, r.url, r.order_by, r.description, r.create_time, r.modify_time FROM tbl_common_role_resource_ref rr LEFT JOIN tbl_common_resource r ON rr.resource_id = r.id WHERE rr.role_id = #{id}
UNION ALL
SELECT r.id, r.parent_id, r.name, r.resource_identifying, r.type, r.url, r.order_by, r.description, r.create_time, r.modify_time FROM tbl_common_resource r WHERE r.type = 1
